<?php if (!defined('BASEPATH')) exit('No direct script access allowed');class Help extends CI_Controller {	private $subnav = array('about'     => 'About',							'contact'   =>'Contact',							'privacy'   =>'Privacy Policy',							'disclaimer'=>'Disclaimer');	// Constructor	function __construct() {		parent::__construct();		// Get User info		$this->load->model('users_model');		$this->user = $this->users_model->getUser(FALSE, FALSE, FALSE); // do not force login, registration, or refresh		//d($this->user, '$this->user');	    if (ENVIRONMENT == 'development') {	      $this->output->enable_profiler(true);	    }	}	// Index	function index() {		$data['user'] = $this->user;		// Send to view		$data['subnav'] = $this->subnav;		$this->load->view('template',$data);	}	// Contact	function about() {		$data['view'] = 'help/about';		$data['title'] = 'About';		$data['user'] = $this->user;		// Send to view		$data['subnav'] = $this->subnav;		$this->load->view('template',$data);	}	// Contact	function contact() {		$data['view'] = 'help/contact';		$data['title'] = 'Contact';		$data['user'] = $this->user;		// Send to view		$data['subnav'] = $this->subnav;		$this->load->view('template',$data);	}	// Privacy Policy	function privacy() {		$data['view'] = 'help/privacy';		$data['title'] = 'Privacy Policy';		$data['user'] = $this->user;		// Send to view		$data['subnav'] = $this->subnav;		$this->load->view('template',$data);	}	// Disclaimer	function disclaimer() {		$data['view'] = 'help/disclaimer';		$data['title'] = 'Disclaimer';		$data['user'] = $this->user;		// Send to view		$data['subnav'] = $this->subnav;		$this->load->view('template',$data);	}}// EOF