<?php if (!defined('BASEPATH')) exit('No direct script access allowed');
class Help extends CI_Controller {		private $slug = 'help';		private $subnav = array('contact'=>'Contact',							'privacy'=>'Privacy Policy',							'disclaimer'=>'Disclaimer');
	private $is_logged_in;		private $facebook_uid;	// Constructor	function __construct() {		parent::__construct();				// Do not require login		$this->is_logged_in = $this->auth->is_logged_in();		$this->facebook_uid = $this->session->userdata('facebook_uid');				$this->output->enable_profiler(TRUE);	}
		// Index
	function index() {		$this->output->enable_profiler(TRUE);				$data['slug'] = 'help';								// Send to view		$data['slug'] = $this->slug;		$data['subnav'] = $this->subnav;		$data['is_logged_in'] = $this->is_logged_in;		$data['facebook_id'] = $this->facebook_uid;		$this->load->view('template',$data);
	}		// Contact	function contact() {			$data['view'] = 'help/contact';		$data['title'] = 'Contact';				// Send to view		$data['slug'] = $this->slug;		$data['subnav'] = $this->subnav;		$this->load->view('template',$data);			}		// Privacy Policy	function privacy() {			$data['view'] = 'help/privacy';		$data['title'] = 'Privacy Policy';				// Send to view		$data['slug'] = $this->slug;		$data['subnav'] = $this->subnav;		$this->load->view('template',$data);			}		// Disclaimer	function disclaimer() {			$data['view'] = 'help/disclaimer';		$data['title'] = 'Disclaimer';				// Send to view		$data['slug'] = $this->slug;		$data['subnav'] = $this->subnav;		$this->load->view('template',$data);			}
}// EOF