<?php if (!defined('BASEPATH')) exit('No direct script access allowed');class Items extends CI_Controller {				private $data = array();		// Constructor	function __construct() {		parent::__construct();				// Force login		$this->auth->require_login();				// Current Page		$this->data['slug'] = 'items';				// Get user details		$this->load->model('users_model');		$this->data['user'] = $this->users_model->get_user_by_facebook_id($this->session->userdata('facebook_uid'));						// Check (and update) facebook access_token		// not needed on this page (only on settings page?)		// $this->load->model('fizzlebizzle');		// $result = $this->fizzlebizzle->get_access_token();		// if ($result['is_true']) {			// $this->session->set_userdata(array('access_token' => $result['access_token']));		// } else {			// $this->session->set_userdata(array('access_token' => FALSE));		// }						$this->load->model('items_model');				$this->output->enable_profiler(TRUE);	}	// Index	function index() {										// get all items		//$this->data['items'] = $this->items_model->get_items();								// Send to view		$this->load->view('template',$this->data);	}		function add() {				// Send to view		$this->data['slug'] = 'add_item';		$this->load->view('template',$this->data);	}		}// EOF