<?php if (!defined('BASEPATH')) exit('No direct script access allowed');class Items extends CI_Controller {		private $data = array();		// Constructor	function __construct() {		parent::__construct();						// Get user details		$this->load->model('users_model');				$this->load->model('items_model');			    if (ENVIRONMENT == 'development') {	      $this->output->enable_profiler(true);	    }	}	// Index	function index() {				// get all items		//$this->data['items'] = $this->items_model->get_items();								// Send to view		$this->load->view('template', $this->data);	}		function add() {				// Send to view		$this->load->view('template', $this->data);	}}// EOF